mapper.select:
select 

,

from TBL_API_CONFIG


    apiname = #{apiname,jdbcType=VARCHAR}


    AND apiip = #{apiip,jdbcType=VARCHAR}


    AND apiport = #{apiport,jdbcType=VARCHAR}


    AND apipath = #{apipath,jdbcType=VARCHAR}


    AND queryparams = #{queryparams,jdbcType=VARCHAR}


    AND sqlparams = #{sqlparams,jdbcType=VARCHAR}


    AND dsid = #{dsid,jdbcType=INTEGER}


    AND publish_flag = #{publishFlag,jdbcType=CHAR}


    AND create_by = #{createBy,jdbcType=VARCHAR}


    AND create_date = #{createDate,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=VARCHAR}


    AND update_date = #{updateDate,jdbcType=TIMESTAMP}


    AND remarks = #{remarks,jdbcType=VARCHAR}


    AND del_flag = #{delFlag,jdbcType=CHAR}


    AND query_sql = #{querySql,jdbcType=LONGVARCHAR}

mapper.delete:
delete from TBL_API_CONFIG


    apiname = #{apiname,jdbcType=VARCHAR}


    AND apiip = #{apiip,jdbcType=VARCHAR}


    AND apiport = #{apiport,jdbcType=VARCHAR}


    AND apipath = #{apipath,jdbcType=VARCHAR}


    AND queryparams = #{queryparams,jdbcType=VARCHAR}


    AND sqlparams = #{sqlparams,jdbcType=VARCHAR}


    AND dsid = #{dsid,jdbcType=INTEGER}


    AND publish_flag = #{publishFlag,jdbcType=CHAR}


    AND create_by = #{createBy,jdbcType=VARCHAR}


    AND create_date = #{createDate,jdbcType=TIMESTAMP},


    AND update_by = #{updateBy,jdbcType=VARCHAR}


    AND update_date = #{updateDate,jdbcType=TIMESTAMP},


    AND remarks = #{remarks,jdbcType=VARCHAR}


    AND del_flag = #{delFlag,jdbcType=CHAR}


    AND query_sql = #{querySql,jdbcType=LONGVARCHAR}
