mapper.select:
select 

from TBL_API_SOURCE


    dsid = #{dsid,jdbcType=INTEGER}


    AND dbtype = #{dbtype,jdbcType=VARCHAR}


    AND driverclassname = #{driverclassname,jdbcType=VARCHAR}


    AND url = #{url,jdbcType=VARCHAR}


    AND username = #{username,jdbcType=VARCHAR}


    AND password = #{password,jdbcType=VARCHAR}


    AND initialsize = #{initialsize,jdbcType=INTEGER}


    AND maxactive = #{maxactive,jdbcType=INTEGER}


    AND minidle = #{minidle,jdbcType=INTEGER}


    AND maxwait = #{maxwait,jdbcType=INTEGER}


    AND removeabandoned = #{removeabandoned,jdbcType=CHAR}


    AND removeabandonedtimeout = #{removeabandonedtimeout,jdbcType=INTEGER}


    AND timebetweenevictionrunsmillis = #{timebetweenevictionrunsmillis,jdbcType=INTEGER}


    AND minevictableidletimemillis = #{minevictableidletimemillis,jdbcType=INTEGER}


    AND validationquery = #{validationquery,jdbcType=VARCHAR}


    AND testwhileidle = #{testwhileidle,jdbcType=CHAR}


    AND testonborrow = #{testonborrow,jdbcType=CHAR}


    AND testonreturn = #{testonreturn,jdbcType=CHAR}


    AND poolpreparedstatements = #{poolpreparedstatements,jdbcType=CHAR}


    AND maxpoolprestatperconnsize = #{maxpoolprestatperconnsize,jdbcType=INTEGER}


    AND filters = #{filters,jdbcType=VARCHAR}


    AND hooktime = #{hooktime,jdbcType=INTEGER}


    AND hooks = #{hooks,jdbcType=VARCHAR}


    AND create_by = #{createBy,jdbcType=VARCHAR}


    AND create_date = #{createDate,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=VARCHAR}


    AND update_date = #{updateDate,jdbcType=TIMESTAMP}


    AND remarks = #{remarks,jdbcType=VARCHAR}


    AND del_flag = #{delFlag,jdbcType=CHAR}

